@Composable
fun MyColumn() {
    Column(
        modifier = Modifier
            .fillMaxWidth()
            .background(Color.LightGray)
            .padding(16.dp),
        verticalArrangement = Arrangement.Center, // выравнивание по вертикали
        horizontalAlignment = Alignment.CenterHorizontally // выравнивание по горизонтали
    ) {
        Text("Первый элемент", style = MaterialTheme.typography.h6)
        Spacer(modifier = Modifier.height(8.dp)) // промежуток
        Text("Второй элемент")
        Text("Третий элемент")
    }
}

@Composable
fun MyRow() {
    Row(
        modifier = Modifier
            .fillMaxWidth()
            .background(Color.LightGray)
            .padding(16.dp),
        horizontalArrangement = Arrangement.SpaceBetween, // расположение по горизонтали
        verticalAlignment = Alignment.CenterVertically // выравнивание по вертикали
    ) {
        Text("Левый")
        Text("Центр")
        Text("Правый")
    }
}
