fun Modifier.animateOnClick(): Modifier = composed {
    var isPressed by remember { mutableStateOf(false) }

    Modifier
        .graphicsLayer {
            scaleX = if (isPressed) 0.95f else 1f
            scaleY = if (isPressed) 0.95f else 1f
        }
        .clickable(
            interactionSource = remember { MutableInteractionSource() },
            indication = null
        ) { /* действие */ }
}

fun Modifier.cardElevation(): Modifier = composed {
    Modifier
        .shadow(
            elevation = 4.dp,
            shape = RoundedCornerShape(8.dp),
            clip = true
        )
        .background(Color.White, RoundedCornerShape(8.dp))
        .padding(16.dp)
}

@Composable
fun ProductCard(product: Product) {
    Column(modifier = Modifier.cardElevation()) {
        Image(/* ... */)
        Text(product.name)
        Text("$${product.price}")
    }
}

fun Modifier.animatedButton(): Modifier = composed {
    var pressed by remember { mutableStateOf(false) }

    Modifier
        .graphicsLayer {
            scaleX = if (pressed) 0.95f else 1f
            scaleY = if (pressed) 0.95f else 1f
            alpha = if (pressed) 0.8f else 1f
        }
        .background(
            color = Color.Blue,
            shape = RoundedCornerShape(8.dp)
        )
        .padding(horizontal = 24.dp, vertical = 12.dp)
        .clickable(
            interactionSource = remember { MutableInteractionSource() },
            indication = null
        ) { /* действие */ }
}
