// Data class to represent each tab item
data class TabItem(
    val title: String,
    val selectedIcon: ImageVector,
    val unselectedIcon: ImageVector
)

@Composable
fun PrimaryTabRowExample() {
    val tabItems = listOf(
        TabItem("Home", Icons.Filled.Home, Icons.Outlined.Home),
        TabItem("Profile", Icons.Filled.Person, Icons.Outlined.Person),
        TabItem("Favorites", Icons.Filled.Favorite, Icons.Outlined.FavoriteBorder)
    )

    val pagerState = rememberPagerState(initialPage = 0) {
        tabItems.size
    }
    val coroutineScope = rememberCoroutineScope()

    Column(modifier = Modifier.fillMaxSize()) {
        PrimaryTabRow(
            selectedTabIndex = pagerState.currentPage,
            containerColor = Color.White, // Set your desired background color here
            contentColor = Color.White // Color text and image
            ,
            indicator = {
                TabRowDefaults.PrimaryIndicator(
                    modifier = Modifier.tabIndicatorOffset(
                        selectedTabIndex = pagerState.currentPage,
                        matchContentSize = false // matchContentSize = true - подстраивает под размер таба, matchContentSize = false - полный размер
                    ),
                    width = Dp.Unspecified,
                    color = Color.Black,
                )
            }
        ) {
            tabItems.forEachIndexed { index, item ->
                Tab(
                    selected = index == pagerState.currentPage,
                    onClick = {
                        coroutineScope.launch {
                            pagerState.animateScrollToPage(index)
                        }
                    },
                    text = { Text(item.title) },
                    icon = {
                        Icon(
                            imageVector = if (index == pagerState.currentPage) item.selectedIcon else item.unselectedIcon,
                            contentDescription = item.title
                        )
                    }
                )
            }
        }

        HorizontalPager(
            state = pagerState,
            modifier = Modifier.fillMaxSize()
        ) { page ->
            // Content for each tab
            Text(text = "Content for ${tabItems[page].title} Tab", modifier = Modifier.fillMaxSize())
        }
    }
}
