dependencies {
    // Preferences DataStore
    implementation "androidx.datastore:datastore-preferences:1.0.0"

    // Proto DataStore (опционально)
    implementation "androidx.datastore:datastore:1.0.0"
    implementation "com.google.protobuf:protobuf-javalite:3.18.0"
}

// Создание в Kotlin (используйте делегат)
val Context.dataStore by preferencesDataStore(
    name = "settings" // Имя файла
)

// Определение ключей
object PreferencesKeys {
    val USER_NAME = stringPreferencesKey("user_name")
    val NOTIFICATIONS_ENABLED = booleanPreferencesKey("notifications_enabled")
    val LAST_UPDATE_TIME = longPreferencesKey("last_update_time")
}

suspend fun saveUserName(name: String) {
    context.dataStore.edit { preferences ->
        preferences[PreferencesKeys.USER_NAME] = name
    }
}

syntax = "proto3";

option java_package = "com.example.app.datastore";
option java_multiple_files = true;

message UserPreferences {
    string user_name = 1;
    bool notifications_enabled = 2;
    int64 last_update_time = 3;
}

object UserPreferencesSerializer : Serializer<UserPreferences> {
    override val defaultValue = UserPreferences.getDefaultInstance()

    override suspend fun readFrom(input: InputStream): UserPreferences {
        try {
            return UserPreferences.parseFrom(input)
        } catch (e: Exception) {
            throw CorruptionException("Cannot read proto", e)
        }
    }

    override suspend fun writeTo(t: UserPreferences, output: OutputStream) {
        t.writeTo(output)
    }
}

// Запись
suspend fun updateUserName(name: String) {
    context.userPreferencesStore.updateData { prefs ->
        prefs.toBuilder().setUserName(name).build()
    }
}

// Чтение
val userPrefsFlow: Flow<UserPreferences> = context.userPreferencesStore.data

val Context.dataStore by preferencesDataStore(
    name = "settings",
    produceMigrations = { context ->
        listOf(SharedPreferencesMigration(
            context,
            "legacy_prefs_name" // Имя старых SharedPreferences
        ))
    }
)

suspend fun manualMigration() {
    val oldPrefs = context.getSharedPreferences("old_prefs", Context.MODE_PRIVATE)
    val newData = oldPrefs.all.mapValues { (_, value) ->
        // Преобразование значений
        when (value) {
            is String -> value
            is Int -> value.toString()
            else -> null
        }
    }

    context.dataStore.edit { prefs ->
        newData.forEach { (key, value) ->
            if (value != null) {
                prefs[stringPreferencesKey(key)] = value
            }
        }
    }
}

class SettingsViewModel(
    private val dataStore: DataStore<Preferences>
) : ViewModel() {

    val uiState = dataStore.data
        .map { prefs ->
            SettingsUiState(
                userName = prefs[PreferencesKeys.USER_NAME] ?: "",
                notificationsEnabled = prefs[PreferencesKeys.NOTIFICATIONS_ENABLED] ?: false
            )
        }
        .stateIn(
            scope = viewModelScope,
            started = SharingStarted.WhileSubscribed(5000),
            initialValue = SettingsUiState()
        )

    fun updateUserName(name: String) {
        viewModelScope.launch {
            dataStore.edit { prefs ->
                prefs[PreferencesKeys.USER_NAME] = name
            }
        }
    }
}
