AlertDialog.Builder(context)
    .setTitle("Подтверждение")
    .setMessage("Вы уверены, что хотите удалить этот элемент?")
    .setPositiveButton("Да") { dialog, _ ->
        // Действие при нажатии "Да"
        dialog.dismiss()
    }
    .setNegativeButton("Нет") { dialog, _ ->
        // Действие при нажатии "Нет"
        dialog.dismiss()
    }
    .setNeutralButton("Отмена") { dialog, _ ->
        dialog.dismiss()
    }
    .create()
    .show()

class MyDialogFragment : DialogFragment() {
    override fun onCreateDialog(savedInstanceState: Bundle?): Dialog {
        return AlertDialog.Builder(requireContext())
            .setTitle("Диалог во Fragment")
            .setMessage("Это диалог, созданный через DialogFragment")
            .setPositiveButton("OK", null)
            .create()
    }
}

// Показать диалог из Activity/Fragment
MyDialogFragment().show(supportFragmentManager, "my_dialog")

