class Person {
    private String name;
    private int age;

    @Override
    public boolean equals(Object o) {
        if (this == o) return true;  // Проверка ссылки
        if (o == null || getClass() != o.getClass()) return false;  // Проверка типа
        Person person = (Person) o;
        return age == person.age && Objects.equals(name, person.name);  // Сравнение полей
    }

    @Override
    public int hashCode() {
        return Objects.hash(name, age);  // Обязательно переопределять вместе с equals()!
    }
}

// Использование
Person p1 = new Person("Анна", 25);
Person p2 = new Person("Анна", 25);
System.out.println(p1.equals(p2));  // true (сравниваются поля)
