content.value:
for (i in 1..5) {
    println(i)  // 1, 2, 3, 4, 5
} 

for (letter in 'A'..'D') {
    println(letter)  // A, B, C, D
}

val array: Array<Int> = arrayOf(10, 5, 32, 63, 17)
for (element in array){
    println(element) //10, 5, 32, 63, 17
    //что-то делаем с element, - элементом, index не доступен
}

val array: Array<Int> = arrayOf(10, 15, 33, 61, 17)
for (index in 0 until array.size){
    println(array[index]) //10, 15, 33, 61, 17
    //что-то делаем с index – индексом, индексы 0 до array.size - 1 (аналог 0..array.lastIndex). , array[index] - элемент
}

val array: Array<Int> = arrayOf(10, 15, 33, 61, 17)
for ((index, value) in array.withIndex()){
    println(index)
    println(value)
    //аналог for (value in array)
    //index — текущий индекс элемента (начинается с 0).
    //value — значение элемента массива.
}

val fruits = listOf("Apple", "Banana", "Orange")
for (fruit in fruits) {
    println(fruit)  // Apple, Banana, Orange
    //что-то делаем с fruit, - элементом, index не доступен
}

for ((index, fruit) in fruits.withIndex()) {
    println("$index: $fruit")  // 0: Apple, 1: Banana, 2: Orange
}
