listOf(1, 2, 3).forEach { element ->
    println(element)  // 1, 2, 3
    //element — текущий элемент, но без индекса
    //Нельзя изменить массив, так как element — это копия!
}

listOf("Aaa", "Bbb", "Ccc").forEachIndexed { index, value ->
    println("$index: $value")  // 0: Aaa, 1: Bbb, 2: Ccc
    //функциональный аналог for ((index, value) in array.withIndex()).
    //index — текущий индекс элемента (начинается с 0).
    //value — значение элемента коллекции.

}
