val numbers = listOf(1, 2, 3, 4, 5)

// Есть ли хотя бы одно четное число?
val hasEven = numbers.any { it % 2 == 0 }
println(hasEven) // true

// Есть ли числа больше 10?
val hasGreaterThanTen = numbers.any { it > 10 }
println(hasGreaterThanTen) // false

val numbers = listOf(2, 4, 6, 8, 10)

// Все числа четные?
val allEven = numbers.all { it % 2 == 0 }
println(allEven) // true

val mixedNumbers = listOf(2, 4, 5, 8, 10)

// Все числа четные?
val allEvenMixed = mixedNumbers.all { it % 2 == 0 }
println(allEvenMixed) // false

val emptyList = emptyList<Int>()

// На пустой коллекции all всегда возвращает true!
println(emptyList.all { it > 10 }) // true
