with(объект) {
    // В этом блоке `this` — это переданный объект
    // Можно обращаться к его свойствам и методам напрямую
}

class Person(var name: String, var age: Int)

val person = Person("Иван", 30)

with(person) {
    println("Имя: $name, Возраст: $age")  // Доступ к свойствам без `person.`
    age += 1                              // Изменение свойства
}

val personInfo = with(person) {
    "Имя: $name, Возраст: $age"  // Это строка будет возвращена
}
println(personInfo)  // "Имя: Иван, Возраст: 31"
