// Пример с возвращаемым значением
fun sum(a: Int, b: Int): Int {
    return a + b
}

// Сокращенная форма (single-expression функции)
fun sum(a: Int, b: Int) = a + b // Так же тут понятно, какой тип у возвращаемого значения, так что его можно не указывать

greet()          // Привет, Гость!
greet("Иван")    // Привет, Иван!

fun greet(name: String = "Гость") {
    println("Привет, $name!")
}

createUser(age = 25, name = "Анна")  // Порядок не важен

fun createUser(name: String, age: Int, isActive: Boolean = true) {
    println("Имя: $name, Возраст: $age, Активен: $isActive")
}

printAll("Привет", "Kotlin", "!")  // Можно передать любое количество строк

fun printAll(vararg messages: String) {
    for (m in messages) println(m)
}
