class Person(val name: String, val age: Int) {
    operator fun component1() = name
    operator fun component2() = age
}

fun main() {
    val person = Person("Alice", 25)
    val (name, age) = person // деструктуризация
    println("$name, $age years old") // Alice, 25 years old
}

data class User(val login: String, val email: String)

fun main() {
    val user = User("admin", "admin@example.com")
    val (username, email) = user
    println("$username: $email") // admin: admin@example.com
}
