enum class Direction {
    NORTH, SOUTH, EAST, WEST
}

// Доступ ко всем константам:
val allDirections = Direction.values() // [NORTH, SOUTH, EAST, WEST]

// Или через enumValues<T>():
enumValues<Direction>().forEach { println(it) }

enum class Color(val rgb: Int) {
    RED(0xFF0000),
    GREEN(0x00FF00),
    BLUE(0x0000FF)
}

fun main() {
    println(Color.RED.rgb) // 16711680 (0xFF0000 в десятичном)
}
