class Person {
    var name: String = "Иван"  // Автоматический геттер и сеттер
    val age: Int = 25          // Только геттер (val — неизменяемое)
}

// Использование
val person = Person()
println(person.name)  // Вызывается name.get()
person.name = "Петр"  // Вызывается name.set("Петр")

class Rectangle(val width: Int, val height: Int) {
    val area: Int
        get() = width * height  // Вычисляемое свойство
}

val rect = Rectangle(10, 20)
println(rect.area)  // 200 (вычисляется при каждом новом обращении)

class User {
    var name: String = ""
        set(value) {
            if (value.isNotBlank()) {
                field = value  // `field` — специальное поле для свойства
            }
        }
}

val user = User()
user.name = ""    // Игнорируется (условие не выполнено)
user.name = "Анна" // OK

class BankAccount {
    var balance: Double = 0.0
        private set  // Сеттер доступен только внутри класса

    fun deposit(amount: Double) {
        balance += amount
    }
}

val account = BankAccount()
account.deposit(100.0)
// account.balance = 200.0  // Ошибка: setter private
